import React, { Component } from "react";
import Zmage from "react-zmage";
import Fade from "react-reveal";

let id = 0;
class Portfolio extends Component {
  constructor(props) {
    super(props);
    this.state = { activeIndex: 0 };
    this.timelineRef = React.createRef();
    this.itemRefs = [];
    this.observer = null;
  }

  componentDidMount() {
    this.setupObserver();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.data !== this.props.data) {
      this.setupObserver();
    }
  }

  componentWillUnmount() {
    if (this.observer) {
      this.observer.disconnect();
      this.observer = null;
    }
  }

  setupObserver() {
    if (!this.props.data || !this.props.data.projects) return;
    if (!this.timelineRef.current) return;
    if (this.observer) {
      this.observer.disconnect();
      this.observer = null;
    }

    this.observer = new IntersectionObserver(
      (entries) => {
        let best = null;
        entries.forEach((entry) => {
          if (!best || entry.intersectionRatio > best.intersectionRatio) {
            best = entry;
          }
        });
        if (best && best.isIntersecting) {
          const index = Number(best.target.dataset.index);
          if (index !== this.state.activeIndex) {
            this.setState({ activeIndex: index });
          }
        }
      },
      {
        root: this.timelineRef.current,
        threshold: [0.4, 0.6, 0.8]
      }
    );

    this.itemRefs.forEach((ref) => {
      if (ref && ref.current) {
        this.observer.observe(ref.current);
      }
    });
  }

  render() {
    if (!this.props.data) return null;
    this.itemRefs = [];
    const projects = this.props.data.projects.map((projects, index) => {
      let projectImage =
        process.env.PUBLIC_URL + "/images/portfolio/" + projects.image;
      const itemRef = React.createRef();
      this.itemRefs.push(itemRef);

      return (
        <div
          key={id++}
          ref={itemRef}
          data-index={index}
          className={
            "portfolio-item timeline-item" +
            (this.state.activeIndex === index ? " is-active" : "")
          }
        >
          <div className="timeline-marker">
            <span className="timeline-dot"></span>
          </div>
          <div className="item-wrap portfolio-card">
            <div className="portfolio-image">
              <Zmage alt={projects.title} src={projectImage} />
            </div>
            <div className="portfolio-content">
              <h3 className="portfolio-title">{projects.title}</h3>
              <p className="portfolio-category">{projects.category}</p>
              {projects.tech ? (
                <div className="portfolio-tags">
                  {projects.tech.map((tag) => (
                    <span key={tag} className="portfolio-tag">
                      {tag}
                    </span>
                  ))}
                </div>
              ) : null}
            </div>
          </div>
        </div>
      );
    });

    return (
      <section id="portfolio">
        <Fade left duration={1000} distance="40px">
          <div className="row">
            <div className="twelve columns collapsed">
              <h1>Check Out Some of My Works.</h1>

              <div
                id="portfolio-wrapper"
                className="portfolio-timeline"
                ref={this.timelineRef}
              >
                {projects}
              </div>
            </div>
          </div>
        </Fade>
      </section>
    );
  }
}

export default Portfolio;
